                        <Grid item xs={6}>
                          <Typography component="p">
                            No.telp : {item.phone}
                          </Typography>
                        </Grid>
                        <Grid item xs={6} className={classes.linkMore}>
                          <Button variant="contained">
                              <Link to={{
                                  pathname: '/detail',
                                  id: `${item.id}`,
                                }}>
                                Read More
                              </Link>
                          </Button>
                          <Button variant="contained">
                            <Link to={{
                                pathname: '/editPost',
                                id: `${item.id}`,
                              }}>
                              Edit
                            </Link>
                          </Button>
                          <Button variant="contained" onClick={()=>this.deletePost(item.id)}>
                              Hapus
                          </Button>
                        </Grid>
                      </Grid>

                    </Paper>

                  </Grid>
                ))}
              </Grid>

            </Container>
        );
    }
}

export default withStyles(styles)(home)
